    backgroundColor: "red",
    color: "white",
    // position: "absolute",
    // right: resp ? "12vw" : "10vw",
    borderRadius: "1.5rem",
    // marginLeft: "1.2rem",
    // textDecoration: "none",
    p: "10px",
    "&:hover": { backgroundColor: "white", color: "red" , border:"2px solid red"},
  });

  const RenderData =  () => {
    const details =   data.map((detail) =>
    // in checks the index presence of index value
      // (detail.id-1) in currentItems ? (
      currentItems.includes(detail.id) ? (
        <span key={detail.id}>
          <Paper elevation={3} sx={{ mb: "1rem" }}>
            <Grid
              container
              style={{
                height: "10rem",
                width: "96%",
                display: "flex",
                alignItems: "center",
                borderRadius: "1rem",
                padding: "5px",
              }}
            >
              <Grid item xs={2.4}>
                {detail.company.name}
              </Grid>
              <Grid item xs={2.4}>
                <b>
                  <p>CONTACT</p>
                </b>
                {detail.phone}
              </Grid>
              <Grid item xs={2.4}>
                <b>
                  <p>CITY</p>
                </b>
                {detail.address.city}
              </Grid>
              <Grid item xs={2.4}>
                <b>
                  <p>STATE</p>
                </b>
                Mumbai-{detail.id}
              </Grid>
              <Grid item xs={2.4}>
                <ShowButton>Show details</ShowButton>
              </Grid>
            </Grid>
          </Paper>
        </span>
      ) : null
    );
    return <> {details} </>;

    // return (
    //   <>
    //     {currentItems &&
    //       data.map((detail) =>
    //       detail.id in currentItems ? (
    //         <span key={detail.id}>
    //         <Paper elevation={3} sx={{ mb: "1rem" }}>
    //           <Grid
    //             container
    //             style={{
    //               height: "10rem",
    //               width: "96%",
    //               display: "flex",
    //               alignItems: "center",
    //               borderRadius: "1rem",
    //               padding: "5px",
    //             }}
    //           >
    //             <Grid item xs={2.4}>
    //               {detail.company.name}
    //             </Grid>
    //             <Grid item xs={2.4}>
    //               <b>
    //                 <p>CONTACT</p>
    //               </b>
    //               {detail.phone}
    //             </Grid>
    //             <Grid item xs={2.4}>
    //               <b>
    //                 <p>CITY</p>
    //               </b>
    //               {detail.address.city}
    //             </Grid>
    //             <Grid item xs={2.4}>
    //               <b>
    //                 <p>STATE</p>
    //               </b>
    //               Mumbai-{detail.id}
    //             </Grid>
    //             <Grid item xs={2.4}>
    //               <ShowButton>Show details</ShowButton>
    //             </Grid>
    //           </Grid>
    //         </Paper>
    //       </span>
    //       ) : null) }
    //   </>
    // );
  }


  return (
    <>
      <Items currentItems={currentItems} />
      <Stack spacing={2}>
        <ReactPaginate
          nextLabel="next >"
          onPageChange={handlePageClick}
          pageRangeDisplayed={3}
          marginPagesDisplayed={2}
          pageCount={pageCount}
          previousLabel="< previous"
          pageClassName="page-item"
          pageLinkClassName="page-link"
          previousClassName="page-item"
          previousLinkClassName="page-link"
          nextClassName="page-item"
          nextLinkClassName="page-link"
          breakLabel="..."
          breakClassName="page-item"
          breakLinkClassName="page-link"
          containerClassName={"pagination"}
          subContainerClassName={"pages pagination"}
          activeClassName={"active"}
          renderOnZeroPageCount={null}
        />
        {/* <Pagination count={pageCount} color="secondary" 
      onChange={handlePageClick}/> */}
      </Stack>

      <RenderData />
    </>
  );
};

export default PaginatedItems;
